mapper.update:
update sportzplatform.event_category
set parent_category_id = #{parentCategoryId,jdbcType=INTEGER},
  event_category_name = #{eventCategoryName,jdbcType=VARCHAR},
  event_category_desc = #{eventCategoryDesc,jdbcType=VARCHAR}
where event_category_id = #{eventCategoryId,jdbcType=INTEGER}

mapper.select:
select event_category_id, parent_category_id, event_category_name, event_category_desc
from sportzplatform.event_category
where event_category_id = #{eventCategoryId,jdbcType=INTEGER}
