mapper.insert:
insert into sportzplatform.event_organizer (event_refid, tournament_refid, organizer_role
  )
values (#{eventRefid,jdbcType=INTEGER}, #{tournamentRefid,jdbcType=INTEGER}, #{organizerRole,jdbcType=VARCHAR}
  )

mapper.update:
update sportzplatform.event_organizer
set event_refid = #{eventRefid,jdbcType=INTEGER},
  tournament_refid = #{tournamentRefid,jdbcType=INTEGER},
  organizer_role = #{organizerRole,jdbcType=VARCHAR}
where event_organizer_id = #{eventOrganizerId,jdbcType=INTEGER}

mapper.select:
select event_organizer_id, event_refid, tournament_refid, organizer_role
from sportzplatform.event_organizer
where event_organizer_id = #{eventOrganizerId,jdbcType=INTEGER}
