mapper.update:
update sportzplatform.follower
set follower_userid = #{followerUserid,jdbcType=INTEGER},
  company_refid = #{companyRefid,jdbcType=INTEGER},
  user_refid = #{userRefid,jdbcType=INTEGER},
  followed_from = #{followedFrom,jdbcType=DATE}
where follower_id = #{followerId,jdbcType=INTEGER}

mapper.select:
select follower_id, follower_userid, company_refid, user_refid, followed_from
from sportzplatform.follower
where follower_id = #{followerId,jdbcType=INTEGER}
