mapper.update:
update sportzplatform.organizer
set company_refid = #{companyRefid,jdbcType=INTEGER},
  user_refid = #{userRefid,jdbcType=INTEGER}
where organizer_id = #{organizerId,jdbcType=INTEGER}

mapper.select:
select organizer_id, company_refid, user_refid
from sportzplatform.organizer
where organizer_id = #{organizerId,jdbcType=INTEGER}
