mapper.update:
update sportzplatform.sports
set sports_name = #{sportsName,jdbcType=VARCHAR},
  sports_desc = #{sportsDesc,jdbcType=VARCHAR},
  sports_format = #{sportsFormat,jdbcType=OTHER,typeHandler=com.skiya.sportzplatform.common.JsonTypeHandler},
  sports_category = #{sportsCategory,jdbcType=OTHER,typeHandler=com.skiya.sportzplatform.common.JsonTypeHandler}
where sports_id = #{sportsId,jdbcType=INTEGER}

mapper.select:
select sports_id, sports_name, sports_desc, sports_format, sports_category
from sportzplatform.sports
where sports_id = #{sportsId,jdbcType=INTEGER}

select sports_id, sports_name, sports_desc, sports_format, sports_category
from sportzplatform.sports
where sports_name = #{sportsName,jdbcType=VARCHAR}
