mapper.update:
update sportzplatform.team_player
set team_refid = #{teamRefid,jdbcType=INTEGER},
  participant_refid = #{participantRefid,jdbcType=INTEGER},
  played_from = #{playedFrom,jdbcType=DATE},
  played_to = #{playedTo,jdbcType=DATE}
where team_player_id = #{teamPlayerId,jdbcType=INTEGER}

mapper.select:
select team_player_id, team_refid, participant_refid, played_from, played_to
from sportzplatform.team_player
where team_player_id = #{teamPlayerId,jdbcType=INTEGER}
