mapper.update:
update sportzplatform.tournament_groups
set group_name = #{groupName,jdbcType=VARCHAR},
  tournament_category_refid = #{tournamentCategoryRefid,jdbcType=INTEGER},
  tournament_refid = #{tournamentRefid,jdbcType=INTEGER}
where group_id = #{groupId,jdbcType=INTEGER}

mapper.select:
select group_id, group_name, tournament_category_refid, tournament_refid
from sportzplatform.tournament_groups
where group_id = #{groupId,jdbcType=INTEGER}
