mapper.insert:
insert into sportzplatform.tournament_registrations (player_id, registration_date, team_id
  )
values (#{playerId,jdbcType=INTEGER}, #{registrationDate,jdbcType=TIMESTAMP}, #{teamId,jdbcType=INTEGER}
  )

mapper.update:
update sportzplatform.tournament_registrations
set player_id = #{playerId,jdbcType=INTEGER},
  registration_date = #{registrationDate,jdbcType=TIMESTAMP},
  team_id = #{teamId,jdbcType=INTEGER}
where registration_id = #{registrationId,jdbcType=INTEGER}

mapper.select:
select registration_id, player_id, registration_date, team_id
from sportzplatform.tournament_registrations
where registration_id = #{registrationId,jdbcType=INTEGER}
